
public class localvariables {
	
	void sub(int a,int b) {
		
		int sub=(a-b);
		System.out.println("sub="+sub);
	}
	
	
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		localvariables obj  =  new localvariables();
		obj.sub(67,78);
		obj.sub(90, 20);
		obj.sub(30, 20);
		obj.sub(35, 57);

	}

}
